mapper.update:
UPDATE FREEBOARD
    SET
        TITLE = #{title},
        CONTENT = #{content},
        MODDATE = #{moddate}
    WHERE ID = #{id}

UPDATE FREEBOARD_FILE
    SET
        FILENAME = #{filename},
        FILEORIGINNAME = #{fileoriginname},
        FILEPATH = #{filepath},
        FILETYPE = #{filetype}
    WHERE ID = #{id}
      AND BOARD_ID = #{board_id}

mapper.delete:
DELETE FROM FREEBOARD_FILE
    WHERE ID = #{id}
      AND BOARD_ID = #{board_id}
